#include "LeetCode.h"


class Solution {
public:
	int sumRootToLeaf(TreeNode* root) {
		vector<int> path;
		return DFS(root, path);
	}

	int DFS(TreeNode* node, vector<int> &path) {
		int s = 0;
		if (node->left == NULL && node->right == NULL) {
			for (int n : path) {
				s = s * 2 + n;
			}
			s = s * 2 + node->val;
			return s;
		}
		else {
			path.push_back(node->val);
			if (node->left != NULL) {
				s += DFS(node->left, path);
			}
			if (node->right != NULL) {
				s += DFS(node->right, path);
			}
			path.pop_back();
			return s;
		}
	}
};

